import axios from 'axios';

const host = 'http://localhost:5000';
const Api = async (method, url, data, headers) => {
  try {
    return await axios({
      method: method,
      baseURL: `${host}${url}`,
      headers: headers,
      data: data,
    });
  } catch (e) {
    console.log('Erros');
    throw e;
  }
};

export default Api;
